import React, { useState, useEffect } from 'react';
import FileBase from 'react-file-base64';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';

import useStyles from './styles';
import { createPost, updatePost } from '../../actions/posts';

//Get the current ID 

const Form = ({ currentId, setCurrentId }) => {
  const [postData, setPostData] = useState({
    creator: '',
    title: '',
    message: '',
    tags: '',
    selectedFile: '',
  });
  const post= useSelector((state)=> currentId ? state.posts.find((p)=>p._id === currentId) : null)
  const classes = useStyles();
  const dispatch = useDispatch();


  useEffect(()=>{
    if(post) setPostData(post)
  },[post])


  const handleSubmit = (e) => {
    e.preventDefault();
if(currentId) {
    dispatch(updatePost(currentId,postData))
  } else
  {
    dispatch(createPost(postData))
  };
    
  };



  const clear = ()=>{};

  return (
    <Paper className={classes.paper}>
      <form
        autoComplete="off"
        noValidate
        onSubmit={handleSubmit}
        className={`${classes.root} ${classes.form}`}
      >
        <Typography variant="h6">Creating a Memory</Typography>
        <TextField
          name="creator"
          variant="outlined"
          label="Creator"
          fullWidth
          value={postData.creator}
          onChange={(e) => setPostData({ ...postData, creator: e.target.value })}
        ></TextField>
        <TextField
          name="title"
          variant="outlined"
          label="Title"
          fullWidth
          value={postData.title}
          onChange={(e) => setPostData({ ...postData, title: e.target.value })}
        ></TextField>
        <TextField
          name="message"
          variant="outlined"
          label="Message"
          fullWidth
          value={postData.message}
          onChange={(e) => setPostData({ ...postData, message: e.target.value })}
        ></TextField>
        <TextField
          name="tags"
          variant="outlined"
          label="Tags"
          fullWidth
          value={postData.tags}
          onChange={(e) => setPostData({ ...postData, tags: e.target.value })}
        ></TextField>
        <div
          className={classes.fileInput}
        >
          <FileBase 
            type='file'
            multiple={false}
            onDone={({base64})=>setPostData({...postData, selectedFile: base64})}/>
        </div>
        <Button size='large' type='submit' fullWidth color='primary' variant='contained' className={classes.buttonSubmit} >Submit</Button>
        <Button size='small' onClick={clear} fullWidth color='secondary' variant='contained'>Clear</Button>
      </form>
    </Paper>
  );
};

export default Form;
